CREATE TABLE IF NOT EXISTS ycbusi.`TYY_CALL_RECORD_@MONTH`  (`SERIAL_ID` varchar(64) NOT NULL COMMENT '主键',`CALLER` varchar(20) DEFAULT NULL COMMENT '主叫',`CALLED` varchar(20) DEFAULT NULL COMMENT '被叫',`TELX` varchar(20) DEFAULT NULL COMMENT 'X号码',`BIND_ID` varchar(64) DEFAULT NULL COMMENT '绑定ID',`BEGIN_TIME` varchar(19) DEFAULT NULL COMMENT '开始时间',`END_TIME` varchar(19) DEFAULT NULL COMMENT '开始时间',`CLEAR_CAUSE` int DEFAULT NULL COMMENT '挂机原因',`CALL_TYPE` int DEFAULT NULL COMMENT '呼叫类型：1 小号 2 双呼 3短信',`RECORD_FILE` varchar(200) DEFAULT NULL COMMENT '录音路径',`ENT_ID` varchar(19) DEFAULT NULL COMMENT '企业ID',`ENT_NAME` varchar(100) DEFAULT NULL COMMENT '企业名称',`CALL_TIME` char(10) DEFAULT NULL COMMENT '通话时长',`FEE_TIME_6` int DEFAULT NULL COMMENT '6秒计费时长',`FEE_TIME_60` int DEFAULT NULL COMMENT '60秒计费时长',`CREATE_TIME` varchar(19) DEFAULT NULL COMMENT '呼叫创建时间',`CALL_ID` varchar(50) DEFAULT NULL COMMENT '通话ID',`ALERTING_TIME` varchar(19) DEFAULT NULL COMMENT '振铃时间',`ALERTING_DURATION` int DEFAULT NULL COMMENT '振铃时长',`CALL_DURATION` int DEFAULT NULL COMMENT '呼叫时长',`MONTH_ID` int DEFAULT NULL COMMENT '月份ID',`DATE_ID` int DEFAULT NULL COMMENT '日期ID',`CALL_DIRECCTION` int DEFAULT NULL COMMENT '呼叫类型：1呼入 6呼出',`AREA_CODE` varchar(10) DEFAULT NULL COMMENT '区域编号',`HOUR_ID` int DEFAULT NULL,`DEVICE_NO` varchar(64) DEFAULT NULL,`IVR_NO` varchar(64) DEFAULT NULL,`SBC_ADDR` varchar(255) DEFAULT NULL COMMENT 'sbc地址',PRIMARY KEY (`SERIAL_ID`),KEY `INDEX2` (`CALLED`) USING BTREE,KEY `INDEX3` (`BEGIN_TIME`,`TELX`) USING BTREE,KEY `INDEX4` (`CALL_ID`) USING BTREE,KEY `INDEX5` (`TELX`) USING BTREE,KEY `INDEX1` (`BEGIN_TIME`,`CALLER`) USING BTREE,KEY `INDEX6` (`CREATE_TIME`) USING BTREE) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='AXB话单表';